  };

  const getSubjectDistribution = () => {
    const totalCredits = subjects.reduce((sum, subject) => sum + subject.credits, 0);
    
    return subjects.map(subject => ({
      name: subject.name,
      value: subject.credits,
      percentage: Math.round((subject.credits / totalCredits) * 100),
      color: subject.color,
      status: getSubjectAverageGrade(subject.id) >= 4.5 ? 'excellent' : 
              getSubjectAverageGrade(subject.id) >= 4.0 ? 'good' : 
              getSubjectAverageGrade(subject.id) >= 3.5 ? 'satisfactory' : 'needs_improvement'
    }));
  };

  const value = {
    // Ð”Ð°Ð½Ð½Ñ‹Ðµ
    student,
    subjects,
    grades,
    schedule,
    tasks,
    notifications,
    news,
    achievements,
    settings,
    
    // Ð’Ñ‹Ñ‡Ð¸ÑÐ»ÑÐµÐ¼Ñ‹Ðµ Ð·Ð½Ð°Ñ‡ÐµÐ½Ð¸Ñ
    getSubjectById,
    getGradesBySubject,
    calculateAverageGrade,
    getSubjectAverageGrade,
    getAttendanceRate,
    getCompletedTasksCount,
    getTotalTasksCount,
    getUnreadNotificationsCount,
    getScheduleForDay,
    getTasksForToday,
    getOverdueTasks,
    getUpcomingTasks,
    getGradesChartData,
    getSubjectsChartData,
    getPerformanceAnalytics,
    getSubjectDistribution,
    
    // Ð¤ÑƒÐ½ÐºÑ†Ð¸Ð¸ Ð¸Ð·Ð¼ÐµÐ½ÐµÐ½Ð¸Ñ Ð´Ð°Ð½Ð½Ñ‹Ñ…
    updateStudent,
    addGrade,
    updateGrade,
    addTask,
    updateTask,
    deleteTask,
    addQuickTask,
    updateTaskTitle,
    toggleTaskCompleted,
    markNotificationAsRead,
    markAllNotificationsAsRead,
    deleteNotification,
    addNews,
    updateSettings,
    addAchievement,
    syncWithProfile
  };

  return (
    <DataContext.Provider value={value}>
      {children}
    </DataContext.Provider>
  );
};
