import React, { useState, useEffect, useRef } from 'react';
import { useData } from '../contexts/DataContext';
import { format } from 'date-fns';
import { ru } from 'date-fns/locale';
import { 
  MessageSquare, 
  Send, 
  Bot, 
  User, 
  Clock, 
  MessageCircle, 
  CheckCircle,
  Headphones,
  ThumbsUp,
  ThumbsDown,
  Star,
  Download,
  Search,
  Settings,
  Phone,
  Mail,
  MapPin,
  HelpCircle,
  Smile,
  Paperclip,
  Camera,
  MoreHorizontal,
  X,
  Trash2,
  Flag,
  Image,
  File
} from 'lucide-react';
import { CircularProgressbar, buildStyles } from 'react-circular-progressbar';
import 'react-circular-progressbar/dist/styles.css';
import CountUp from 'react-countup';
import './Feedback.css';

const Feedback = () => {
  const { student } = useData();

  // ÐÐµÐ¼ÐµÐ´Ð»ÐµÐ½Ð½Ñ‹Ð¹ ÑÐºÑ€Ð¾Ð»Ð» Ð½Ð°Ð²ÐµÑ€Ñ… Ð¿Ñ€Ð¸ Ð¼Ð¾Ð½Ñ‚Ð¸Ñ€Ð¾Ð²Ð°Ð½Ð¸Ð¸ ÐºÐ¾Ð¼Ð¿Ð¾Ð½ÐµÐ½Ñ‚Ð°
  React.useLayoutEffect(() => {
    window.scrollTo(0, 0);
  }, []);

  // Ð¡Ð¾ÑÑ‚Ð¾ÑÐ½Ð¸Ñ Ñ‡Ð°Ñ‚Ð°
  const [messages, setMessages] = useState(() => {
    const saved = localStorage.getItem('feedback-chat');
    if (saved) {
      return JSON.parse(saved);
    }
    return [
      {
        id: 1,
        type: 'system',
        message: 'Ð”Ð¾Ð±Ñ€Ð¾ Ð¿Ð¾Ð¶Ð°Ð»Ð¾Ð²Ð°Ñ‚ÑŒ Ð² ÑÐ»ÑƒÐ¶Ð±Ñƒ Ð¿Ð¾Ð´Ð´ÐµÑ€Ð¶ÐºÐ¸ UniPortal! ÐšÐ°Ðº Ð´ÐµÐ»Ð°?',
        timestamp: new Date(Date.now() - 1000 * 60 * 5),
        sender: 'Ð¡Ð¸ÑÑ‚ÐµÐ¼Ð°',
        avatar: null,
        status: 'delivered'
      },
      {
